SELECT * FROM (SELECT AVG(DATE_PART('year', AGE(CURRENT_DATE, Н_ЛЮДИ.ДАТА_РОЖДЕНИЯ))) AS AGE, Н_УЧЕНИКИ.ГРУППА FROM Н_ЛЮДИ JOIN Н_УЧЕНИКИ ON Н_УЧЕНИКИ.ЧЛВК_ИД=Н_ЛЮДИ.ИД GROUP BY Н_УЧЕНИКИ.ГРУППА) AS FOO WHERE FOO.AGE <= (SELECT MIN(DATE_PART('year', AGE(CURRENT_DATE, Н_ЛЮДИ.ДАТА_РОЖДЕНИЯ))) AS AGGE FROM Н_ЛЮДИ JOIN Н_УЧЕНИКИ ON Н_УЧЕНИКИ.ЧЛВК_ИД=Н_ЛЮДИ.ИД WHERE Н_УЧЕНИКИ.ГРУППА='1101');
