 SELECT Н_УЧЕНИКИ.ГРУППА,Н_ЛЮДИ.ИД,Н_ЛЮДИ.ФАМИЛИЯ,Н_ЛЮДИ.ИМЯ,Н_ЛЮДИ.ОТЧЕСТВО,Н_ФОРМЫ_ОБУЧЕНИЯ.ИД,Н_УЧЕНИКИ.СОСТОЯНИЕ FROM Н_УЧЕНИКИ JOIN Н_ПЛАНЫ ON Н_ПЛАНЫ.НАПС_ИД=Н_УЧЕНИКИ.ИД JOIN Н_ФОРМЫ_ОБУЧЕНИЯ ON Н_ФОРМЫ_ОБУЧЕНИЯ.ИД = Н_ПЛАНЫ.ФО_ИД JOIN Н_ОБУЧЕНИЯ ON Н_ОБУЧЕНИЯ.ЧЛВК_ИД=Н_УЧЕНИКИ.ЧЛВК_ИД JOIN Н_ЛЮДИ ON Н_ЛЮДИ.ИД = Н_УЧЕНИКИ.ЧЛВК_ИД JOIN Н_НАПРАВЛЕНИЯ_СПЕЦИАЛ ON Н_НАПРАВЛЕНИЯ_СПЕЦИАЛ.ИД=Н_ПЛАНЫ.НАПС_ИД JOIN Н_НАПР_СПЕЦ ON Н_НАПР_СПЕЦ.ИД=Н_НАПРАВЛЕНИЯ_СПЕЦИАЛ.НС_ИД WHERE Н_УЧЕНИКИ.НАЧАЛО < '2012-09-01' AND (Н_ФОРМЫ_ОБУЧЕНИЯ.НАИМЕНОВАНИЕ='Очная' OR Н_ФОРМЫ_ОБУЧЕНИЯ.НАИМЕНОВАНИЕ='Заочная') AND Н_НАПР_СПЕЦ.КОД_НАПРСПЕЦ='230101';
